INSERT INTO `evaluacion_inicial_sst` ( `id_cliente`, `ciclo`, `estandar`, `detalle_estandar`, `estandares_minimos`, `numeral`, `numerales_del_cliente`, `siete`, `veintiun`, `sesenta`, `item_del_estandar`, `evaluacion_inicial`, `valor`, `puntaje_cuantitativo`, `item`, `criterio`, `modo_de_verificacion`, `calificacion`, `nivel_de_evaluacion`, `observaciones`) VALUES
(999, 'I. PLANEAR', 'RECURSOS (10%)', 'Recursos financieros, técnicos humanos y de otra índole requeridos para coordinar y desarrollar el Sistema de Gestion de la Seguridad y Salud en el Trabajo (SG-SST) (4 %)', '7-21-60 - Asignación de responsable', '1.1.1', 60, 7, 21, 60, '1.1.1. Responsable del Sistema de Gestión de Seguridad y Salud en el Trabajo SG-SST', 'CUMPLE TOTALMENTE', '0.50', '0.50', 'Asignación de responsable', 'Asignar una persona que cumpla con el siguiente perfil: El diseño e implementacion del Sistema de Gestion de SST Podra ser realzado  por profesioanles en SST, profesionales con posgrado en SST que cuenten con licencia en Seguridad y Salud en el Trabajo vigente y el curso de capacitacion virtual de cincuenta (50) horas.', 'Solicitar el documento en el que consta la asignacion, con la respectiva determinacion de responsabilidaes y constatar la hoja de vida con soportes de la persona asignada.', '0.00', '', ''),
(999, 'I. PLANEAR', 'RECURSOS (10%)', 'Recursos financieros, técnicos humanos y de otra índole requeridos para coordinar y desarrollar el Sistema de Gestion de la Seguridad y Salud en el Trabajo (SG-SST) (4 %)', '60 - Asignacion de responsabilidades en SST', '1.1.2', 60, 0, 0, 60, '1.1.2 Responsabilidades en el Sistema de Gestión de Seguridad y Salud en el Trabajo – SG-SST', 'NO CUMPLE', '0.50', '0.00', 'Asignacion de responsabilidades en SST', 'Asignar y documentar las responsabilidades específicas en el Sistema de Gestión SST a todos los niveles de la organización, para el desarrollo y mejora continua de dicho Sistema.', 'Solicitar el soporte que contenga la asignación de las responsabilidades en SST.', '0.00', '', ''),
(999, 'I. PLANEAR', 'RECURSOS (10%)', 'Recursos financieros, técnicos humanos y de otra índole requeridos para coordinar y desarrollar el Sistema de Gestion de la Seguridad y Salud en el Trabajo (SG-SST) (4 %)', '21-60 - Asignación de recursos para el Sistema de Gestión en SST', '1.1.3', 60, NULL, 21, 60, '1.1.3 Asignación de recursos para el Sistema de Gestión en Seguridad y Salud en el Trabajo – SG-SST', 'NO APLICA', '0.50', '0.50', 'Asignación de recursos para el Sistema de Gestión en SST', 'Definir y asignar el talento humano, los recursos financieros, técnicos y tecnológicos, requeridos para la implementación, mantenimiento y continuidad del Sistema de Gestión de SST.', 'Constatar la existencia de evidencias físicas que demuestren la definición y asignación del talento humano, los recursos financieros, técnicos y de otra índole para la implementación, mantenimiento y continuidad del Sistema de Gestión de SST, evidenciando la asignación de recursos con base en el plan de trabajo anual.', NULL, '', NULL),
(999, 'I. PLANEAR', 'RECURSOS (10%)', 'Recursos financieros, técnicos humanos y de otra índole requeridos para coordinar y desarrollar el Sistema de Gestion de la Seguridad y Salud en el Trabajo (SG-SST) (4 %)', '7-21-60 - Afiliación al Sistema de Seguridad Social Integral', '1.1.4', 60, 7, 21, 60, '1.1.4 Afiliación al Sistema General de Riesgos Laborales', 'CUMPLE TOTALMENTE', '0.50', '0.50', 'Afiliación al Sistema de Seguridad Social Integral', 'Garantizar que todos los trabajadores, independientemente de su forma de vinculación o contratación están afiliados al Sistema de Seguridad Social en Salud, Pensión y Riesgos Laborales.', 'Solicitar una lista de los trabajadores vinculados laboralmente a la fecha y comparar con la planilla de pago de aportes a la seguridad social de los cuatro (4) meses anteriores a la fecha de verificación...', NULL, '', NULL),
(999, 'I. PLANEAR', 'RECURSOS (10%)', 'Recursos financieros, técnicos humanos y de otra índole requeridos para coordinar y desarrollar el Sistema de Gestión de la Seguridad y Salud en el Trabajo (SG-SST) (4 %)', '60 - Identificación de trabajadores que se dediquen en forma permanente a actividades de alto riesgo y cotización de pensión especial', '1.1.5', 60, NULL, NULL, 60, '1.1.5 Identificación de trabajadores de alto riesgo y cotización de pensión especial', 'NO APLICA', '0.50', '0.50', 'Identificación de trabajadores que se dediquen en forma permanente a actividades de alto riesgo y cotización de pensión especial', 'En el caso que aplique, identificar a los trabajadores que se dediquen en forma permanente al ejercicio de las actividades de alto riesgo establecidas en el Decreto 2090 de 2003 o de las normas que lo adicionen, modifiquen o complementen y cotizar el monto establecido en la norma, al Sistema de Pensiones.', 'En los casos en que aplique, verificar si se tienen identificados los trabajadores que se dedican en forma permanente al ejercicio de las actividades de alto riesgo de que trata el Decreto 2090 de 2003 y si se ha realizado el pago de la cotización especial señalado en dicha norma.', NULL, '', NULL),
(999, 'I. PLANEAR', 'RECURSOS (10%)', 'Recursos financieros, técnicos humanos y de otra índole requeridos para coordinar y desarrollar el Sistema de Gestión de la Seguridad y Salud en el Trabajo (SG-SST) (4 %)', '21-60 - Conformación y funcionamiento del COPASST', '1.1.6', 60, NULL, 21, 60, '1.1.6 Conformación COPASST', 'NO APLICA', '0.50', '0.50', 'Conformación y funcionamiento del COPASST', 'Conformar y garantizar el funcionamiento del Comité Paritario de Seguridad y Salud en el Trabajo - COPASST.', 'Solicitar los soportes de la convocatoria, elección, conformación del Comité Paritario de Seguridad y Salud en el Trabajo y el acta de constitución...', NULL, '', NULL),
(999, 'I. PLANEAR', 'RECURSOS (10%)', 'Recursos financieros, técnicos humanos y de otra índole requeridos para coordinar y desarrollar el Sistema de Gestión de la Seguridad y Salud en el Trabajo (SG-SST) (4 %)', '7-60 - Capacitación de los integrantes del COPASST', '1.1.7', 60, 7, NULL, 60, '1.1.7 Capacitación COPASST', 'CUMPLE TOTALMENTE', '0.50', '0.50', 'Capacitación de los integrantes del COPASST', 'Capacitar a lo integrantes del COPASST para el cumplimiento efectivo de las responsabilidades que les asigna la ley.', 'Solicitar documentos que evidencien las actividades de capacitación brindada a los integrantes del COPASST.', NULL, '', NULL),
(999, 'I. PLANEAR', 'RECURSOS (10%)', 'Recursos financieros, técnicos humanos y de otra índole requeridos para coordinar y desarrollar el Sistema de Gestión de la Seguridad y Salud en el Trabajo (SG-SST) (4 %)', '21-60 - Conformación y funcionamiento del Comité de Convivencia Laboral', '1.1.8', 60, NULL, 21, 60, '1.1.8 Conformación Comité de Convivencia', 'NO APLICA', '0.50', '0.50', 'Conformación y funcionamiento del Comité de Convivencia Laboral', 'Conformar y garantizar el funcionamiento del Comité de Convivencia Laboral de acuerdo con la normatividad vigente.', 'Solicitar el documento de conformación del Comité de Convivencia Laboral y verificar que esté integrado de acuerdo a la normativa y que se encuentra vigente...', NULL, '', NULL),
(999, 'I. PLANEAR', 'RECURSOS (10%)', 'Capacitación en el Sistema de Gestión de Seguridad y Salud en el Trabajo (6 %)', '21-60 - Programa de capacitación anual', '1.2.1', 60, NULL, 21, 60, '1.2.1 Programa Capacitación promoción y prevención PYP', 'NO APLICA', '2.00', '2.00', 'Programa de capacitación anual', 'Elaborar y ejecutar el programa de capacitación anual en promoción y prevención, que incluye lo referente a los peligros/riesgos prioritarios y las medidas de prevención y control, extensivo a todos los niveles de la organización.', 'Solicitar el programa de capacitación anual y la matriz de identificación de peligros y verificar que el mismo esté dirigido a los peligros ya identificados y esté acorde con la evaluación y control de los riesgos y/o necesidades en Seguridad y Salud en el Trabajo...', NULL, '', NULL),
(999, 'I. PLANEAR', 'RECURSOS (10%)', 'Capacitación en el Sistema de Gestión de Seguridad y Salud en el Trabajo (6 %)', '60 - Inducción y reinducción en SST', '1.2.2', 60, NULL, NULL, 60, '1.2.2 Inducción y Reinducción en Sistema de Gestión de Seguridad y Salud en el Trabajo SG-SST, actividades de Promoción y Prevención PyP', 'NO APLICA', '2.00', '2.00', 'Inducción y reinducción en SST', 'Realizar actividades de inducción y reinducción, las cuales deben estar incluidas en el programa de capacitación, dirigidas a todos los trabajadores, independientemente de su forma de vinculación y/o contratación, de manera previa al inicio de sus labores, en aspectos generales y específicos de las actividades o funciones a realizar que incluya entre otros, la identificación de peligros y control de los riesgos en su trabajo y la prevención de accidentes de trabajo y enfermedades laborales.', 'Solicitar la lista de trabajadores, participantes independientemente de su forma de vinculación y/o contratación, y verificar los soportes documentales que den cuenta de la inducción y reinducción de conformidad con el criterio...', NULL, '', NULL),
(999, 'I. PLANEAR', 'RECURSOS (10%)', 'Capacitación en el Sistema de Gestión de Seguridad y Salud en el Trabajo (6 %)', '60 - Curso Virtual de capacitación de cincuenta (50) horas en SST.', '1.2.3', 60, NULL, NULL, 60, '1.2.3 Responsables del Sistema de Gestión de Seguridad y Salud en el Trabajo SG-SST con curso virtual de 50 horas', 'NO APLICA', '2.00', '2.00', 'Curso Virtual de capacitación de cincuenta (50) horas en SST.', 'El responsable del Sistema de Gestión de SST realiza el curso de capacitación virtual de cincuenta (50) horas en SST definido por el Ministerio del Trabajo.', 'Solicitar el certificado de aprobación del curso de capacitación virtual de cincuenta (50) horas en SST definido por el Ministerio del Trabajo...', NULL, '', NULL),
(999, 'I. PLANEAR', 'GESTION INTEGRAL DEL SISTEMA DE GESTIÓN DE LA SEGURIDAD Y LA SALUD EN EL TRABAJO (15%)', 'Política de Seguridad y Salud en el Trabajo (1%)', '21-60 - Política de Seguridad  y Salud en el Trabajo.', '2.1.1', 60, NULL, 21, 60, '2.1.1 Política del Sistema de Gestión de Seguridad y Salud en el Trabajo SG-SST firmada, fechada y comunicada al COPASST', 'NO APLICA', '1.00', '1.00', 'Política de Seguridad  y Salud en el Trabajo.', 'Establecer por escrito la Política de Seguridad y Salud en el Trabajo y comunicarla al Comité Paritario de Seguridad y Salud en el Trabajo - COPASST. La Política debe ser fechada y firmada por el representante legal y expresa el compromiso de la alta dirección, el alcance sobre todos los centros de trabajo y todos sus trabajadores independientemente de su forma de vinculación y/o contratación, es revisada, como mínimo una vez al año, hace parte de las políticas de gestión de la empresa, se encuentra difundida y accesible para todos los niveles de la organización. Incluye como mínimo el compromiso con: - La identificación de los peligros, evaluación y valoración de los riesgos y con los respectivos controles. - Proteger la seguridad y salud de todos los trabajadores mediante la mejora continua. - El cumplimiento de la normatividad vigente aplicable en materia de riesgos laborales.', 'Solicitar la política del Sistema de Gestión de SST de la empresa y confirmar que cumpla con los aspectos contenidos en el criterio. Validar para la revisión anual de la política como mínimo: fecha de emisión, firmada por el representante legal actual, que estén incluidos los requisitos normativos actuales. Entrevistar a los miembros del COPASST para indagar el conocimiento de la política en SST.', NULL, NULL, NULL),
(999, 'I. PLANEAR', 'GESTION INTEGRAL DEL SISTEMA DE GESTIÓN DE LA SEGURIDAD Y LA SALUD EN EL TRABAJO (15%)', 'Objetivos del Sistema de Gestión de la Seguridad y la Salud en el Trabajo SG-SST (1%)', '60 - Objetivos de SST', '2.2.1', 60, NULL, NULL, 60, '2.2.1 Objetivos definidos, claros, medibles, cuantificables, con metas, documentados, revisados del SG-SST', 'NO APLICA', '1.00', '1.00', 'Objetivos de SST', 'Definir los objetivos del Sistema de Gestión de SST de conformidad con la política de SST, los cuales deben ser claros, medibles, cuantificables y tener metas.', 'Revisar si los objetivos se encuentran definidos, cumplen con las condiciones mencionadas en el criterio y si existen evidencias del proceso de difusión.', NULL, '', NULL),
(999, 'I. PLANEAR', 'GESTION INTEGRAL DEL SISTEMA DE GESTIÓN DE LA SEGURIDAD Y LA SALUD EN EL TRABAJO (15%)', 'Evaluación inicial del SG-SST (1%)', '60 - Evaluación Inicial del Sistema de Gestión', '2.3.1', 60, NULL, NULL, 60, '2.3.1 Evaluación e identificación de prioridades', 'NO APLICA', '1.00', '1.00', 'Evaluación Inicial del Sistema de Gestión', 'Realizar la evaluación inicial del Sistema de Gestión de SST, identificando las prioridades para establecer el plan de trabajo anual o para la actualización del existente.', 'Solicitar la evaluación inicial del Sistema de Gestión de SST mediante la matriz legal, matriz de peligros y evaluación de riesgos, verificación de controles, lista de asistencia a capacitaciones, análisis de puestos de trabajo, exámenes médicos de ingreso y periódicos y seguimiento de indicadores.', NULL, '', NULL),
(999, 'I. PLANEAR', 'GESTION INTEGRAL DEL SISTEMA DE GESTIÓN DE LA SEGURIDAD Y LA SALUD EN EL TRABAJO (15%)', 'Plan Anual de Trabajo (2%)', '7-21-60 - Plan Anual de Trabajo', '2.4.1', 60, 7, 21, 60, '2.4.1 Plan que identifica objetivos, metas, responsabilidad, recursos con cronograma y firmado', 'CUMPLE TOTALMENTE', '2.00', '2.00', 'Plan Anual de Trabajo', 'Diseñar y definir un plan anual de trabajo para el cumplimiento del Sistema de Gestión de SST, el cual identifica los objetivos, metas, responsabilidades, recursos, cronograma de actividades, firmado por el empleador y el responsable del Sistema de Gestión de SST.', 'Solicitar el plan de trabajo anual. Verificar el cumplimiento del mismo. En el caso que se hayan presentado incumplimientos al plan, solicitar los planes de mejora respectivos.', NULL, '', NULL),
(999, 'I. PLANEAR', 'GESTION INTEGRAL DEL SISTEMA DE GESTIÓN DE LA SEGURIDAD Y LA SALUD EN EL TRABAJO (15%)', 'Conservación de la documentación (2%)', '21-60 - Archivo y retención documental del Sistema de Gestión de SST', '2.5.1', 60, NULL, 21, 60, '2.5.1 Archivo o retención documental del Sistema de Gestión en Seguridad y Salud en el Trabajo SG-SST', 'NO APLICA', '2.00', '2.00', 'Archivo y retención documental del Sistema de Gestión de SST', 'Contar con un sistema de archivo y retención documental, para los registros y documentos que soportan el Sistema de Gestión de SST.', 'Constatar la existencia de un sistema de archivo y retención documental, para los registros y documentos que soportan el Sistema de Gestión de SST...', NULL, '', NULL),
(999, 'I. PLANEAR', 'GESTION INTEGRAL DEL SISTEMA DE GESTIÓN DE LA SEGURIDAD Y LA SALUD EN EL TRABAJO (15%)', 'Rendición de cuentas (1%)', '60 - Rendición de cuentas', '2.6.1', 60, NULL, NULL, 60, '2.6.1 Rendición sobre el desempeño', 'NO APLICA', '1.00', '1.00', 'Rendición de cuentas', 'Realizar anualmente la Rendición de Cuentas del desarrollo del Sistema de Gestión de SST, que incluya a todos los niveles de la empresa.', 'Solicitar los registros documentales que evidencien la rendición de cuentas anual, al interior de la empresa...', NULL, '', NULL),
(999, 'I. PLANEAR', 'GESTION INTEGRAL DEL SISTEMA DE GESTIÓN DE LA SEGURIDAD Y LA SALUD EN EL TRABAJO (15%)', 'Normatividad nacional vigente y aplicable en materia de seguridad y salud en el trabajo (2%)', '60 - Matriz legal', '2.7.1', 60, NULL, NULL, 60, '2.7.1 Matriz legal', 'NO APLICA', '2.00', '2.00', 'Matriz legal', 'Definir la matriz legal que contemple las normas actualizadas del Sistema General de Riesgos Laborales aplicables a la empresa.', 'Solicitar la matriz legal. Verificar que contenga: - Normas vigentes en riesgos laborales, aplicables a la empresa...', NULL, '', NULL),
(999, 'I. PLANEAR', 'GESTION INTEGRAL DEL SISTEMA DE GESTIÓN DE LA SEGURIDAD Y LA SALUD EN EL TRABAJO (15%)', 'Comunicación (1%)', '60 - Mecanismos de comunicación', '2.8.1', 60, NULL, NULL, 60, '2.8.1 Mecanismos de comunicación, auto reporte en Sistema de Gestión de Seguridad y Salud en el Trabajo SG-SST', 'NO APLICA', '1.00', '1.00', 'Mecanismos de comunicación', 'Disponer de mecanismos eficaces para recibir y responder las comunicaciones internas y externas relativas a la Seguridad y Salud en el Trabajo.', 'Constatar la existencia de mecanismos eficaces de comunicación interna y externa que tiene la empresa en materia de Seguridad y Salud en el Trabajo.', NULL, '', NULL),
(999, 'I. PLANEAR', 'GESTION INTEGRAL DEL SISTEMA DE GESTIÓN DE LA SEGURIDAD Y LA SALUD EN EL TRABAJO (15%)', 'Adquisiciones (1%)', '60 - Identificación y evaluación para la adquisición de bienes y servicios', '2.9.1', 60, NULL, NULL, 60, '2.9.1 Identificación, evaluación, para adquisición de productos y servicios en Sistema de Gestión de Seguridad y Salud en el Trabajo SG-SST', 'NO APLICA', '1.00', '1.00', 'Identificación y evaluación para la adquisición de bienes y servicios', 'Establecer un procedimiento para la identificación y evaluación de las especificaciones en SST de las compras y adquisición de productos y servicios.', 'Verificar la existencia de un procedimiento para la identificación y evaluación de las especificaciones en SST de las compras o adquisición de productos y servicios y constatar su cumplimiento.', NULL, '', NULL),
(999, 'I. PLANEAR', 'GESTION INTEGRAL DEL SISTEMA DE GESTIÓN DE LA SEGURIDAD Y LA SALUD EN EL TRABAJO (15%)', 'Contratación (2%)', '60 - Evaluación y selección de proveedores y contratistas', '2.10.1', 60, NULL, NULL, 60, '2.10.1 Evaluación y selección de proveedores y contratistas', 'NO APLICA', '2.00', '2.00', 'Evaluación y selección de proveedores y contratistas', 'Establecer los aspectos de SST que podrá tener en cuenta la empresa en la evaluación y selección de proveedores y contratistas.', 'Solicitar el documento que señale los criterios relacionados con SST para la evaluación y selección de proveedores, cuando la empresa los haya establecido.', NULL, '', NULL),
(999, 'I. PLANEAR', 'GESTION INTEGRAL DEL SISTEMA DE GESTIÓN DE LA SEGURIDAD Y LA SALUD EN EL TRABAJO (15%)', 'Gestión del cambio (1%)', '60 - Gestión del cambio', '2.11.1', 60, NULL, NULL, 60, '2.11.1 Evaluación del impacto de cambios internos y externos en el Sistema de Gestión de Seguridad y Salud en el Trabajo SG-SST', 'NO APLICA', '1.00', '1.00', 'Gestión del cambio', 'Disponer de un procedimiento para evaluar el impacto sobre la Seguridad y Salud en el Trabajo que se pueda generar por cambios internos o externos.', 'Solicitar el documento que contenga el procedimiento.', NULL, '', NULL),
(999, 'II. HACER', 'GESTIÓN DE LA SALUD (20%)', 'Condiciones de salud en el trabajo (9%)', '21-60 - Descripción sociodemográfica y Diagnóstico de las condiciones de salud de los trabajadores', '3.1.1', 60, NULL, 21, 60, '3.1.1 Descripcion sociodemografica.Diagnostico de Condiciones de Salud', 'NO APLICA', '1.00', '1.00', 'Descripción sociodemográfica y Diagnóstico de las condiciones de salud de los trabajadores', 'Recolectar la siguiente información actualizada de todos los trabajadores del último año: la descripción socio demográfica de los trabajadores (edad, sexo, escolaridad, estado civil) y el diagnóstico de condiciones de salud.', 'Solicitar el documento consolidado con la información socio demográfica acorde con lo requerido en el criterio y el diagnóstico de condiciones de salud.', NULL, '', NULL),
(999, 'II. HACER', 'GESTIÓN DE LA SALUD (20%)', 'Condiciones de salud en el trabajo (9%)', '21-60 - Actividades de medicina del trabajo y de prevención y promoción de la Salud.', '3.1.2', 60, NULL, 21, 60, '3.1.2 Actividades de Promoción y Prevención en Salud', 'NO APLICA', '1.00', '1.00', 'Actividades de medicina del trabajo y de prevención y promoción de la Salud.', 'Desarrollar las actividades de medicina del trabajo, prevención y promoción de la salud y programas de vigilancia epidemiológica requeridos.', 'Solicitar las evidencias que constaten la ejecución de las actividades de medicina del trabajo, promoción y prevención.', NULL, '', NULL),
(999, 'II. HACER', 'GESTIÓN DE LA SALUD (20%)', 'Condiciones de salud en el trabajo (9%)', '60 - Perfiles de cargos', '3.1.3', 60, NULL, NULL, 60, '3.1.3 Información al médico de los perfiles de cargo', 'NO APLICA', '1.00', '1.00', 'Perfiles de cargos', 'Informar al médico que realiza las evaluaciones ocupacionales los perfiles de cargos con descripción de las tareas y el medio donde se laborará.', 'Verificar que se remitieron al médico los soportes documentales de los perfiles de cargos.', NULL, '', NULL),
(999, 'II. HACER', 'GESTIÓN DE LA SALUD (20%)', 'Condiciones de salud en el trabajo (9%)', '7-21-60 - Evaluaciones médicas ocupacionales', '3.1.4', 60, 7, 21, 60, '3.1.4 Realización de los evaluaciones médicas ocupacionales: Peligros- Periodicidad Comunicación al Trabajador', 'CUMPLE TOTALMENTE', '1.00', '1.00', 'Evaluaciones médicas ocupacionales', 'Realizar las evaluaciones médicas según la normatividad y peligros/riesgos identificados.', 'Solicitar conceptos de aptitud y comunicación de resultados a los trabajadores.', NULL, '', NULL),
(999, 'II. HACER', 'GESTIÓN DE LA SALUD (20%)', 'Condiciones de salud en el trabajo (9%)', '60 - Custodia de las historias clínicas', '3.1.5', 60, NULL, NULL, 60, '3.1.5 Custodia de Historias Clínicas', 'NO APLICA', '1.00', '1.00', 'Custodia de las historias clínicas', 'Tener la custodia de las historias clínicas a cargo de una institución prestadora de servicios en SST o del médico ocupacional.', 'Evidenciar que la custodia de las historias clínicas está a cargo de una entidad autorizada.', NULL, '', NULL),
(999, 'II. HACER', 'GESTIÓN DE LA SALUD (20%)', 'Condiciones de salud en el trabajo (9%)', '21-60 - Restricciones y recomendaciones médico laborales', '3.1.6', 60, NULL, 21, 60, '3.1.6 Restricciones y recomendaciones médico laborales', 'NO APLICA', '1.00', '1.00', 'Restricciones y recomendaciones médico laborales', 'Cumplir con las recomendaciones médico laborales prescritas por la EPS o ARL.', 'Solicitar documentos y constatar las evidencias del cumplimiento de recomendaciones laborales.', NULL, '', NULL),
